import React, { useState } from 'react';
import './App.scss';
import GuestListComponent from './components/Guest/GuestList';
import GuestList from './model/GuestList';
import data from './data/data.json';
import DisplayTitle from './components/DisplayTitle/DisplayTitle';

export enum DisplayTitleEnum {
    GUESTLIST = 'Guest list',
    ROOMS = 'Room arrangements',
    SCHEDULE = 'Schedule',
}

const App : React.FC = props => {
    const guestList = GuestList.deserialize(data);
    const [selected, setSelected] = useState(DisplayTitleEnum.GUESTLIST);

    return (
    <div className="App">
        <header>
            <h1>Birthdays in Malaga</h1>
        </header>

        <div className={`display-titles flex-row`}>
            <DisplayTitle title={DisplayTitleEnum.GUESTLIST} selected={selected == DisplayTitleEnum.GUESTLIST} setSelected={() => setSelected(DisplayTitleEnum.GUESTLIST)} />
            <DisplayTitle title={DisplayTitleEnum.SCHEDULE} selected={selected == DisplayTitleEnum.SCHEDULE} setSelected={() => setSelected(DisplayTitleEnum.SCHEDULE)} />
            <DisplayTitle title={DisplayTitleEnum.ROOMS} selected={selected == DisplayTitleEnum.ROOMS} setSelected={() => setSelected(DisplayTitleEnum.ROOMS)} />
        </div>

        <GuestListComponent guests={guestList.guests}/>
    </div>
    );
}

export default App;
