import React from 'react';
import { Room } from '../../model/Room';
import './RoomsComponent.scss';
import RoomComponent from './RoomComponent';
import Guest from '../../model/Guest';

interface IRoomsComponentProps {
    rooms : Room[];
    guests : Guest[];
}

const RoomsComponent : React.FC<IRoomsComponentProps> = props => {
    const getGuests = (roomId : number) => {
        return props.guests.filter(t => t.roomId == roomId)
    }

    return (
        <div className={`rooms flex-row`}>
            {props.rooms.map(t => <RoomComponent room={t} guests={getGuests(t.id)}/>)}
        </div>
    );
}

export default RoomsComponent;
