import React, { useState } from 'react';
import './App.scss';
import GuestListComponent from './components/Guest/GuestList';
import GuestList from './model/GuestList';
import data from './data/data.json';

export enum DisplayTitle {
    GUESTLIST = 'Guest list',
    ROOMS = 'Room arrangements',
    SCHEDULE = 'Schedule',
}

const App : React.FC = props => {
    const guestList = GuestList.deserialize(data);
    const [selected, setSelected] = useState(DisplayTitle.GUESTLIST);

    return (
    <div className="App">
        <header>
            <h1>Birthdays in Malaga</h1>
        </header>

        <div className={`display-titles flex-row`}>
            <DisplayTitle title={DisplayTitle.GUESTLIST} selected={selected == DisplayTitle.GUESTLIST} setSelected={() => setSelected(DisplayTitle.GUESTLIST)} />
            <DisplayTitle title={DisplayTitle.SCHEDULE} selected={selected == DisplayTitle.SCHEDULE} setSelected={() => setSelected(DisplayTitle.SCHEDULE)} />
            <DisplayTitle title={DisplayTitle.ROOMS} selected={selected == DisplayTitle.ROOMS} setSelected={() => setSelected(DisplayTitle.ROOMS)} />
        </div>

        <GuestListComponent guests={guestList.guests}/>
    </div>
    );
}

export default App;
