import React from 'react';
import Guest from '../../model/Guest';
import { Room } from '../../model/Room';
import './RoomComponent.scss';

interface IRoomComponentProps {
    room : Room;
    guests : Guest[];
}

const RoomComponent : React.FC<IRoomComponentProps> = props => {
    return (
        <div className={`room`}>
            <p>Bedroom {props.room.id}</p>
            <p>{props.room.bedArrangement}</p>
            {props.guests.map(t => <p>{t.name}</p>)}
        </div>
    );
}

export default RoomComponent;
