import React, { useState } from 'react';
import MapOfParos from '../MapOfParos/MapOfParos';
import './Home.scss';
import LocationComponent from '../Location/LocationComponent';
import Album from '../../model/Album';

interface IHomeProps {
    album : Album;
}

const Home : React.FC<IHomeProps> = props => {
    const [locationNumber, setLocationNumber] = useState(0);
    const locations = props.album.locations;
    const location = locations[locationNumber];

    let Map : React.FC;

    switch (props.album.name) {
        case 'Paros':
            Map = MapOfParos;
        case 'Malte':
            Map = MapOfParos;
        case 'Crète':
            Map = MapOfParos;
        default:
            throw new Error(`Album not recognised : ${props.album.name}`);
    }

    return (
        <div className={`home`}>
            <header>
                <h2>welcome to</h2>
                <h1>{props.album.name}</h1>
            </header>
            <MapOfParos locationNumber={locationNumber} setLocationNumber={setLocationNumber}/>
            <LocationComponent location={location} />
        </div>
    );
}

export default Home;
