import React from 'react';
import './DisplayTitle.scss';

interface IDisplayTitleProps {
    title : string;
    selected : boolean;
    setSelected : () => void;

}

const DisplayTitle : React.FC<IDisplayTitleProps> = props => {
    const selectedClassName = props.selected ? 'selected' : '';
    return (
        <div className={`display-title ${selectedClassName}`} onClick={props.setSelected}>
            {props.title}
        </div>
    );
}

export default DisplayTitle;
